import React, { Component } from 'react';
import TaskItem from './TaskList/TaskItem';
import axios from 'axios';
class TaskList extends Component {
    constructor(props)
    {
        super(props);
        this.state = {
            user : [{
                username : '',
                password : '',
                status : true 
            }]
        }
    }
    
    componentDidMount = () => 
    {
        axios.get(`https://localhost:8080/api/users`)
      .then(res => {
        const user = res.data;
        // this.setState {
        //     username : user.username,
        //     password : username.password,
        //     status : true
        // };
        this.onSetState(user)
      })
      .catch(error => console.log(error));
    }
    
    onSetState = (box) =>
    {
        this.setState = {
            username : box.username,
            password : box.password,
            status : true
        };
    }

    render() {
        var {tasks} = this.state
        var element = tasks.map((task, index) => {
            return <TaskItem key={index} index = {index} task = {task}/>
        });
        return (
            <table className="table table-bordered table-hover">
                <thead>
                    <tr>
                        <th className="text-center">STT</th>
                        <th className="text-center">Username</th>
                        <th className="text-center">Password</th>
                        <th className="text-center">Trang Thai</th>
                        <th className="text-center">Hanh Dong</th>
                    </tr>
                </thead>
                <tbody>
                    <tr>
                        <td></td>
                        <td>
                            <input  
                                type="text"
                                className="form-control"
                                name="filtername"
                            />
                        </td>
                        <td></td>
                        <td>
                            <select
                                className="form-control"
                                name="filtername"
                            >
                                <option value={-1}>Tat ca</option>
                                <option value={0}>An</option>
                                <option value={1}>Kich Hoat</option>
                            </select>
                        </td>
                        <td></td>
                    </tr>
                    
                    {element}
                </tbody>
            </table>
        );
    }

}

export default TaskList;
