import React, { Component } from 'react';
import TaskItem from './TaskList/TaskItem';
import axios from 'axios';
class TaskList extends Component {
    constructor(props)
    {
        super(props);
        this.state = {
            boxuser : {
                
            },
        }
    }
    
    componentDidMount = () => 
    {
        this.setState = {
            boxuser: this.props.task
        }
    }
    
    onSetState = (user) =>
    {
        this.setState ( {
            boxuser : user
        });
        console.log(this.state.boxuser);
    }

    onHandleChange = (event) => {
        var target = event.target;
        var name = target.name;
        var value = target.type === 'checkbox' ? target.checked : target.value;
        this.setState = {
            [name ] : value
        };
        console.log(this.state)
    }

    render() {
        var tasks = this.state.boxuser;
        console.log(tasks)
        var element = tasks.map((task, index) => {
            return <TaskItem key={index} index = {index} task = {task}/>
        });
        return (
            <table className="table table-bordered table-hover">
                <thead>
                    <tr>
                        <th className="text-center">STT</th>
                        <th className="text-center">Username</th>
                        <th className="text-center">Password</th>
                        <th className="text-center">Trang Thai</th>
                        <th className="text-center">Hanh Dong</th>
                    </tr>
                </thead>
                <tbody>
                    <tr>
                        <td></td>
                        <td>
                            <input
                                type="text"
                                className="form-control"
                                name="inputname"
                            />
                        </td>
                        <td></td>
                        <td>
                            <select
                                className="form-control"
                                name="filtername"
                            >
                                <option value={-1}>Tat ca</option>
                                <option value={0}>An</option>
                                <option value={1}>Kich Hoat</option>
                            </select>
                        </td>
                        <td></td>
                    </tr>
                    
                    {element}
                </tbody>
            </table>
        );
    }

}

export default TaskList;
